import React, { useEffect, useRef, useState } from 'react';
import mqtt, { MqttClient } from 'mqtt';
import './App.css';

const MQTT_BROKER = 'wss://test.mosquitto.org:8081';
const TOPIC = 'giardino/stato';
const FAMIGLIE = ['Rossi', 'Bianchi'];

type StatoGiardino = {
  stato: 'libero' | 'occupato';
  famiglia: string;
  timestamp: number;
};

function formatDate(ts: number) {
  const d = new Date(ts);
  return d.toLocaleString();
}

function App() {
  const [famiglia, setFamiglia] = useState('Rossi');
  const [stato, setStato] = useState<StatoGiardino | null>(null);
  const [conn, setConn] = useState<'connecting' | 'connected' | 'error'>('connecting');
  const clientRef = useRef<MqttClient | null>(null);
  const [showError, setShowError] = useState(false);

  useEffect(() => {
    const client = mqtt.connect(MQTT_BROKER);
    clientRef.current = client;

    const timeout = setTimeout(() => {
      if (conn !== 'connected') setShowError(true);
    }, 10000); // 10 secondi

    client.on('connect', () => {
      setConn('connected');
      setShowError(false);
      client.subscribe(TOPIC);
    });
    client.on('error', () => {
      if (conn !== 'connected') setShowError(true);
      setConn('error');
    });
    client.on('message', (topic, msg) => {
      if (topic === TOPIC) {
        try {
          const data = JSON.parse(msg.toString());
          if (data.stato && data.famiglia && data.timestamp) {
            setStato(data);
          }
        } catch {}
      }
    });
    return () => { client.end(); clearTimeout(timeout); };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const pubblicaStato = (nuovoStato: 'libero' | 'occupato') => {
    if (clientRef.current && clientRef.current.connected) {
      const payload: StatoGiardino = {
        stato: nuovoStato,
        famiglia,
        timestamp: Date.now(),
      };
      clientRef.current.publish(TOPIC, JSON.stringify(payload));
      // Aggiornamento ottimistico locale
      setStato(payload);
    }
  };

  let boxColor = '#2ecc40';
  let boxText = 'Giardino libero';
  let infoText = '';
  if (stato) {
    if (stato.stato === 'occupato') {
      boxColor = '#e74c3c';
      boxText = 'Giardino occupato';
      infoText = `Occupato da: ${stato.famiglia} dal ${formatDate(stato.timestamp)}`;
    } else {
      boxColor = '#2ecc40';
      boxText = 'Giardino libero';
      infoText = `Ultimo utilizzatore: ${stato.famiglia} (liberato il ${formatDate(stato.timestamp)})`;
    }
  }

  if (conn !== 'connected') {
    return (
      <div className="App">
        <header className="App-header">
          <div style={{
            background: '#222',
            color: '#fff',
            borderRadius: 16,
            width: 400,
            height: 220,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: 32,
            fontWeight: 'bold',
            margin: '0 auto',
            boxShadow: '0 4px 24px #0006',
            position: 'relative',
          }}>
            {!showError ? 'Connessione al broker MQTT...' : 'Errore di connessione al broker!'}
            {!showError && (
              <div style={{ marginTop: 24, fontSize: 18, color: '#aaa' }}>Attendere...</div>
            )}
          </div>
        </header>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h2>Gestione Giardino</h2>
        <div style={{ marginBottom: 24 }}>
          <label>
            Seleziona la tua famiglia:
            <select
              value={famiglia}
              onChange={e => setFamiglia(e.target.value)}
              style={{ marginLeft: 8, fontSize: 18 }}
            >
              {FAMIGLIE.map(f => <option key={f} value={f}>{f}</option>)}
            </select>
          </label>
        </div>
        <div style={{ display: 'flex', gap: 16, marginBottom: 32 }}>
          <button
            style={{ fontSize: 18, padding: '10px 24px' }}
            onClick={() => pubblicaStato('occupato')}
            disabled={
              (stato?.stato === 'occupato' && stato?.famiglia === famiglia) ||
              (stato?.stato === 'occupato' && stato?.famiglia !== famiglia)
            }
          >
            Occupa giardino
          </button>
          <button
            style={{ fontSize: 18, padding: '10px 24px' }}
            onClick={() => pubblicaStato('libero')}
            disabled={
              !(stato?.stato === 'occupato' && stato?.famiglia === famiglia)
            }
          >
            Libera giardino
          </button>
        </div>
        <div style={{
          background: boxColor,
          color: '#fff',
          borderRadius: 16,
          width: 400,
          height: 220,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          fontSize: 36,
          fontWeight: 'bold',
          margin: '0 auto',
          boxShadow: '0 4px 24px #0006',
          position: 'relative',
        }}>
          {boxText}
          <div style={{
            position: 'absolute',
            bottom: 18,
            left: 0,
            width: '100%',
            textAlign: 'center',
            fontSize: 18,
            fontWeight: 'normal',
            color: '#fff',
            opacity: 0.95,
          }}>
            {infoText}
          </div>
        </div>
        <div style={{ marginTop: 32, fontSize: 16, color: '#aaa' }}>
          Stato MQTT: {conn === 'connected' ? 'Connesso' : conn === 'connecting' ? 'Connessione...' : 'Errore'}
        </div>
      </header>
    </div>
  );
}

export default App;
