import React, { useEffect, useRef, useState } from 'react';
import mqtt, { MqttClient } from 'mqtt';
import './App.css';

const MQTT_BROKER = 'wss://test.mosquitto.org:8081';

function App() {
  const [topic, setTopic] = useState('test/topic');
  const [message, setMessage] = useState('');
  const [received, setReceived] = useState<string[]>([]);
  const clientRef = useRef<MqttClient | null>(null);

  useEffect(() => {
    // Connect to MQTT broker
    const client = mqtt.connect(MQTT_BROKER);
    clientRef.current = client;

    client.on('connect', () => {
      client.subscribe(topic, (err) => {
        if (!err) {
          setReceived((msgs) => [...msgs, `Subscribed to ${topic}`]);
        }
      });
    });

    client.on('message', (t, msg) => {
      setReceived((msgs) => [
        ...msgs,
        `[${t}] ${msg.toString()}`
      ]);
    });

    return () => {
      client.end();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [topic]);

  const handlePublish = () => {
    if (clientRef.current && clientRef.current.connected) {
      clientRef.current.publish(topic, message);
      setReceived((msgs) => [...msgs, `(You) [${topic}]: ${message}`]);
      setMessage('');
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>MQTT React Demo</h2>
        <div style={{ marginBottom: 16 }}>
          <label>
            Topic:
            <input
              type="text"
              value={topic}
              onChange={(e) => setTopic(e.target.value)}
              style={{ marginLeft: 8 }}
            />
          </label>
        </div>
        <div style={{ marginBottom: 16 }}>
          <input
            type="text"
            placeholder="Messaggio da inviare"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            style={{ width: 200, marginRight: 8 }}
            onKeyDown={(e) => { if (e.key === 'Enter') handlePublish(); }}
          />
          <button onClick={handlePublish} disabled={!message}>
            Pubblica
          </button>
        </div>
        <div style={{ textAlign: 'left', maxWidth: 400 }}>
          <h4>Messaggi ricevuti:</h4>
          <div style={{ background: '#222', padding: 10, borderRadius: 4, minHeight: 100 }}>
            {received.length === 0 ? (
              <span style={{ color: '#888' }}>Nessun messaggio</span>
            ) : (
              received.map((msg, i) => (
                <div key={i} style={{ color: '#fff', fontSize: 14 }}>{msg}</div>
              ))
            )}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
